<?php if($sections): ?><ol <?php if ($level == 1):?> class="sortable" <?php endif ?>>    <?php foreach ($sections as $section): ?>    <li id="list_<?= $section->id ?>">        <div >            <span class="glyphicon glyphicon-move"></span><span class="section_header"> <a href="<?= Yii::app()->createUrl("/sections/view", array("id" => $section->id)) ?>"><?= $section->title ?></a></span>            <span class="pull-right">                <span class="section_type">                    <?= $section->typeName ?>                </span>                <span class="admin-icon">                    <a href="<?= Yii::app()->createUrl("/backend/sections/update", array("id" => $section->id)) ?>" title="Редактировать раздел">                        <i class="glyphicon glyphicon-edit" ></i>                    </a>                    <a class="remove" href="<?= Yii::app()->createUrl("/backend/sections/delete", array("id" => $section->id)) ?>" onClick="return window.confirm('Удалить раздел?')" title="Удалить раздел">                        <i class="glyphicon glyphicon-remove"></i>                    </a>                    <a href="<?= Yii::app()->createUrl("/backend/sections/create", array("p_id" => $section->id)) ?>" title="Добавить подраздел">                        <i class="glyphicon glyphicon-share-alt"></i>                    </a>                </span>            </span>        </div>        <?php if($section->childs): ?>        <?php $this->renderPartial('_recursion', array('sections' => $section->childs, 'level' => $section->level + 1)); ?>        <?php endif; ?>    </li>    <?php endforeach; ?></ol><?php endif; ?>